CREATE TABLE `iklan` (
  `id_iklan` int(11) NOT NULL,
  `id_user` int(11) NOT NULL,
  `isi_iklan` text NOT NULL,
  `gambar` varchar(255) NOT NULL,
  `link_iklan` varchar(255) NOT NULL,
